    // HashMap
    public List<Integer> findSubstring(String s, String[] words) {
        if (s == null || s.length() == 0 || words == null || words.length == 0) {
            return new ArrayList<>();
        }
        List<Integer> resultList = new ArrayList<>(16);
        int minIndex = s.length();
        for (String word : words) {
            int index = s.indexOf(word);
            if (index >= 0 && index < minIndex) {
                minIndex = index;
            }
        }
        Map<String, Integer> map = new HashMap<>(16);
        for (String word : words) {
            if (!map.containsKey(word)) {
                map.put(word, 1);
            } else {
                map.put(word, map.get(word) + 1);
            }
        }
        int len = words[0].length();
        for (int i = minIndex; i <= s.length() - len * words.length; i++) {
            Map<String, Integer> map2 = new HashMap<>();
            for (int j = i; j <= i + len * (words.length - 1); ) {
                String sub = s.substring(j, j + len);
                if (map.containsKey(sub)) {
                    if (!map2.containsKey(sub)) {
                        map2.put(sub, 1);
                    } else {
                        map2.put(sub, map2.get(sub) + 1);
                    }
                    j += len;
                } else {
                    j++;
                }
            }
            boolean find = true;
            for (Map.Entry<String, Integer> entry : map.entrySet()) {
                if (!map2.containsKey(entry.getKey()) || !map2.get(entry.getKey()).equals(entry.getValue())) {
                    find = false;
                    break;
                }
            }
            if (find) {
                resultList.add(i);
            }
            map2.clear();
        }
        return resultList;
    }

    // 效率很低
    public List<Integer> findSubstring1(String s, String[] words) {
        if (s == null || s.length() == 0 || words == null || words.length == 0) {
            return new ArrayList<>();
        }
        List<Integer> resultList = new ArrayList<>();
        int minIndex = s.length();
        for (String word : words) {
            int index = s.indexOf(word);
            if (index >= 0 && index < minIndex) {
                minIndex = index;
            }
        }
        int len = words[0].length();
        List<String> list;
        for (int i = minIndex; i <= s.length() - len * words.length; i++) {
            list = new ArrayList<>(Arrays.asList(words));
            int curr = i;
            while (list.size() > 0) {
                boolean find = false;
                for (int j = list.size() - 1; j >= 0; j--) {
                    String e = list.get(j);
                    if (e.equals(s.substring(curr, curr + len))) {
                        curr += len;
                        list.remove(j);
                        find = true;
                        break;
                    }
                }
                if (!find) {
                    break;
                }
            }
            if (list.size() == 0) {
                resultList.add(i);
            }
        }
        return resultList;
    }
